insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 8, 2, 12, 'Jehov� recompense tu obra, y tu remuneraci�n sea cumplida de parte de Jehov� Dios de Israel, bajo cuyas alas has venido a refugiarte. ');
