insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 8, 2, 19, 'Y le dijo su suegra: �D�nde has espigado hoy? �y d�nde has trabajado? Bendito sea el que te ha reconocido. Y cont� ella a su suegra con qui�n hab�a trabajado, y dijo: El nombre del var�n con quien hoy he trabajado es Booz. ');
