insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 8, 3, 11, 'Ahora pues, no temas, hija m�a; yo har� contigo lo que t� digas, pues toda la gente de mi pueblo sabe que eres mujer virtuosa. ');
