insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 8, 3, 15, 'Despu�s le dijo: Qu�tate el manto que traes sobre ti, y tenlo. Y teni�ndolo ella, �l midi� seis medidas de cebada, y se las puso encima; y ella se fue a la ciudad. ');
