insert into Versiculos (Id_Version, Id_Libro, NumeroCap, NumeroVers, Texto) Values (1, 8, 4, 2, 'Entonces �l tom� a diez varones de los ancianos de la ciudad, y dijo: Sentaos aqu�. Y ellos se sentaron. ');
